# Cache keys for the application

# User cache keys
USER_CACHE_KEY = "user:{user_id}"
USER_SESSION_KEY = "session:{session_id}"

# Product cache keys
PRODUCT_CACHE_KEY = "product:{product_id}"
PRODUCT_LIST_CACHE_KEY = "products:list:{category}"

# Order cache keys
ORDER_CACHE_KEY = "order:{order_id}"
USER_ORDERS_CACHE_KEY = "user:{user_id}:orders"

# Payment cache keys
PAYMENT_CACHE_KEY = "payment:{payment_id}"

# Inventory cache keys
INVENTORY_CACHE_KEY = "inventory:{product_id}"